mapper.insert:
insert into tb_loginlog (


        uid,


        loginCount,


        loginTime


) values (


        #{uid},


        #{loginCount},


        #{loginTime}



)

mapper.select:
select tl.*
from tb_log tl
         inner join tb_user tu on tl.uid = tu.id
         inner join tb_grade tg on tl.gid = tg.id
         inner join tb_bussiness tb on tl.bid = tb.id
         inner join tb_operate top on tl.oid =top.id


                uid = #{uid}


                and bid = #{bid}


                and oid = #{oid}


order by doDate desc
limit 1;
